import React, { useState, useMemo } from 'react';
import { TinyButton as ScrollUpButton } from "react-scroll-up-button";
import './App.css';
import Header from './components/Header'
import FontCard from './components/FontCard'
import Footer from './components/Footer'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import generateQuote from './helpers/generateQuote.function'

enum Mode {
  DarkMode = "DarkMode",
  LightMode = "LightMode"
}

const reallyCoolQuotes: string[] = [
  "Don’t let the noise of others’ opinions drown out your own inner voice.",
  "Your limitation—it’s only your imagination.",
  "Push yourself, because no one else is going to do it for you.",
  "Sometimes later becomes never. Do it now.",
  "Great things never come from comfort zones.",
  "Dream it. Wish it. Do it.",
  "Success doesn’t just find you. You have to go out and get it.",
  "The harder you work for something, the greater you’ll feel when you achieve it.",
  "Dream bigger. Do bigger.",
  "Don’t stop when you’re tired. Stop when you’re done.",
  "Wake up with determination. Go to bed with satisfaction.",
  "Do something today that your future self will thank you for.",
  "Little things make big days.",
  "It’s going to be hard, but hard does not mean impossible."
]

const App: React.FC = () => {
  const [font, setFont] = useState('')
  const [sampleText, setSampleText] = useState("")
  const [fontSize, setFontSize] = useState('12px')
  const [mode, setMode] = useState(Mode.LightMode)
  const defaultQuote = useMemo<string>( generateQuote(reallyCoolQuotes)(), [] )
  // useEffect(() => {
  // },[])

  function handleChange(e: React.FormEvent<HTMLInputElement>) {
    const { id, value } = e.currentTarget
    if (id === "font") {
      setFont(value)
    }
    else if (id === "sampleText") {
      setSampleText(value)
    }
  }

  function handleDropDown(e: React.FormEvent<HTMLSelectElement>) {
    setFontSize(e.currentTarget.value)
  }

  function changeMode(e: React.FormEvent<HTMLButtonElement>) {
    e.preventDefault()
    if (e.currentTarget.value === Mode.DarkMode) {
      setMode(Mode.DarkMode)
    }
    else {
      setMode(Mode.LightMode)
    }
  }

  return (
    <div className={`app-container ${mode === Mode.DarkMode ? "dark-mode" : "light-mode"}`}>
      <Header />
      <div className="font-manager">
        <form>
          <input type="text" placeholder="Search fonts" name="font" id="font" value={font} onChange={handleChange} />
          <input type="text" placeholder="Type something" name="sampleText" id="sampleText" value={sampleText} onChange={handleChange} />
          <select name="fontSize" id="fontSize" onChange={handleDropDown} value={fontSize}>
            <option value="12px">12px</option>
            <option value="24px">24px</option>
            <option value="36px">36px</option>
            <option value="48px">48px</option>
          </select>
          <fieldset>
            <button name="DarkMode" value={Mode.DarkMode} onClick={changeMode}></button>
            <button name="LightMode" value={Mode.LightMode} onClick={changeMode}></button>
          </fieldset>
          <button> <FontAwesomeIcon icon={["fas", "list"]} /> </button>
          <button>  <FontAwesomeIcon icon={["fas", "spinner"]} /></button>
        </form>
      </div>
      <div className="font-card-container">
        <FontCard author="Eu" title="Font Massa" sampleText={sampleText || defaultQuote} fontSize={fontSize} />
        <FontCard author="Eu" title="Font Massa" sampleText={sampleText || defaultQuote} fontSize={fontSize} />
        <FontCard author="Eu" title="Font Massa" sampleText={sampleText || defaultQuote} fontSize={fontSize} />
        <FontCard author="Eu" title="Font Massa" sampleText={sampleText || defaultQuote} fontSize={fontSize} />
        <FontCard author="Eu" title="Font Massa" sampleText={sampleText || defaultQuote} fontSize={fontSize} />
        <FontCard author="Eu" title="Font Massa" sampleText={sampleText || defaultQuote} fontSize={fontSize} />
        <FontCard author="Eu" title="Font Massa" sampleText={sampleText || defaultQuote} fontSize={fontSize} />
        <FontCard author="Eu" title="Font Massa" sampleText={sampleText || defaultQuote} fontSize={fontSize} />
        <FontCard author="Eu" title="Font Massa" sampleText={sampleText || defaultQuote} fontSize={fontSize} />
        <FontCard author="Eu" title="Font Massa" sampleText={sampleText || defaultQuote} fontSize={fontSize} />
        <FontCard author="Eu" title="Font Massa" sampleText={sampleText || defaultQuote} fontSize={fontSize} />
        <FontCard author="Eu" title="Font Massa" sampleText={sampleText || defaultQuote} fontSize={fontSize} />
      </div>
      <ScrollUpButton style={{ backgroundColor: "transparent", fill: mode === Mode.DarkMode ? "lightblue" : "red", outline: "none" }} />
      <Footer />
    </div>
  );
}

export default App;
